    @Override
    public void exportOnline() {
        StudentInfo studentInfo = testService.test3();
        //添加数据
        List<StudentInfo> list = Lists.newArrayList();
        list.add(studentInfo);
        list.add(studentInfo);
        //获取模板
        TemplateExportParams params = new TemplateExportParams("templates/月度考核目标计分表.xlsx");
        params.setColForEach(true);
        Map<String, Object> map = new HashMap<String, Object>(100);
        map.put("list", list);
        //3.执行excel导出
        Workbook workbook = ExcelExportUtil.exportExcel(params, map);
        // 添加将数据进行转换
        SimpleDateFormat dateFormater = new SimpleDateFormat("yyyyMMddHHmmss");
        Date date = new Date();
        ServletRequestAttributes servletRequestAttributes = (ServletRequestAttributes) RequestContextHolder.getRequestAttributes();
        HttpServletResponse response = servletRequestAttributes.getResponse();
        response.setContentType("application/vnd.ms-excel");
        //添加文件名称
        String fileName = "student" + "-" + dateFormater.format(date) + ".xlsx";
        //导出
        try {
            response.setHeader("Content-disposition",
                    "attachment;filename=" + new String(fileName.getBytes("gbk"), "iso-8859-1"));
        } catch (UnsupportedEncodingException e1) {
            e1.printStackTrace();
        }
        try (OutputStream out = response.getOutputStream();) {
            workbook.write(out);
            out.flush();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    @Override
    public ApiResponseEntity exportLocal() {
        //添加数据
        StudentInfo studentInfo = new StudentInfo("陈实施", 12L, 12L, LocalDateTime.now(), LocalDateTime.now(), 0);

        TemplateExportParams params = new TemplateExportParams("templates/easyPoiTemp.xlsx", true);
        Map<String, Object> map = new HashMap<String, Object>(100);
        map.put("studentInfo", studentInfo);
        map.put("stuName", "陈实施");
        map.put("cs", "陈实施");
        //3.执行excel导出
        Workbook workbook = ExcelExportUtil.exportExcel(params, map);
        //4.创建文件存储路径
        File saveFile = new File("excel");
        if (!saveFile.exists()) {
            saveFile.mkdirs();
        }
        FileOutputStream fos;
        String filePath;
        try {
            //4.写入文件
            filePath = saveFile + "\\student.xlsx";
            fos = new FileOutputStream(filePath);
            workbook.write(fos);
            fos.close();
            JSONObject data = new JSONObject();
            data.put("filepath", filePath);
            return ApiResponseEntity.success("数据导出成功！", data);
        } catch (FileNotFoundException e) {
            log.error("FileNotFoundException={}", e.getMessage());
            return ApiResponseEntity.fail("数据导出失败!" + e.getMessage());
        } catch (IOException e) {
            log.error("IOException={}", e.getMessage());
            return ApiResponseEntity.fail("数据导出失败!" + e.getMessage());
        }
    }

    public static void main(String[] args) {
        try  {
        File inputWord = new DefaultResourceLoader().getResource("templates/xjyd_jbxx.docx").getFile();
//        File inputWord = new File("templates/xjyd_jbxx.docx");
            File outputFile = new DefaultResourceLoader().getResource("templates/2.pdf").getFile();
//            File outputFile = new File("templates/xjyd_jbxx.pdf");
            InputStream docxInputStream = new FileInputStream(inputWord);
            OutputStream outputStream = new FileOutputStream(outputFile);
            IConverter converter = LocalConverter.builder().build();
            converter.convert(docxInputStream).as(DocumentType.DOCX).to(outputStream).as(DocumentType.PDF).execute();
            outputStream.close();
            System.out.println("success");
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

